import React, { useState } from "react";

const albumsData = [
    {
        id: 1,
        title: "Midnight Vibes",
        artist: "Lunar Echoes",
        cover: "https://picsum.photos/id/1015/300/300",
        tracks: [
            { id: 101, title: "Nightfall", duration: "4:12" },
            { id: 102, title: "Stellar Drift", duration: "3:45" },
            { id: 103, title: "Cosmic Pulse", duration: "5:02" },
        ],
    },
    {
        id: 2,
        title: "Purple Haze",
        artist: "The Violet Hour",
        cover: "https://picsum.photos/id/1003/300/300",
        tracks: [
            { id: 201, title: "Dreamscape", duration: "3:55" },
            { id: 202, title: "Velvet Night", duration: "4:20" },
            { id: 203, title: "Twilight Glow", duration: "3:30" },
        ],
    },
    // Добавь свои альбомы и треки по желанию
];

export default function MusicApp() {
    const [search, setSearch] = useState("");
    const [selectedAlbum, setSelectedAlbum] = useState(null);

    // Фильтрация альбомов и треков по поиску
    const filteredAlbums = albumsData
        .map((album) => ({
            ...album,
            tracks: album.tracks.filter((t) =>
                t.title.toLowerCase().includes(search.toLowerCase())
            ),
        }))
        .filter(
            (album) =>
                album.title.toLowerCase().includes(search.toLowerCase()) ||
                album.artist.toLowerCase().includes(search.toLowerCase()) ||
                album.tracks.length > 0
        );

    return (
        <div className="app">
            <header>
                <h1>Музыкальный Плеер</h1>
                <input
                    className="search"
                    type="text"
                    placeholder="Искать альбом, исполнителя или трек..."
                    value={search}
                    onChange={(e) => setSearch(e.target.value)}
                    aria-label="Поиск по музыке"
                />
            </header>

            <main>
                {!selectedAlbum ? (
                    <section className="albums">
                        {filteredAlbums.length ? (
                            filteredAlbums.map((album) => (
                                <article
                                    key={album.id}
                                    className="album-card"
                                    onClick={() => setSelectedAlbum(album)}
                                    tabIndex={0}
                                    role="button"
                                    aria-label={`Открыть альбом ${album.title}`}
                                    onKeyDown={(e) => {
                                        if (e.key === "Enter") setSelectedAlbum(album);
                                    }}
                                >
                                    <img src={album.cover} alt={`Обложка альбома ${album.title}`} />
                                    <div className="album-info">
                                        <h2>{album.title}</h2>
                                        <p>{album.artist}</p>
                                    </div>
                                </article>
                            ))
                        ) : (
                            <p className="no-results">Ничего не найдено</p>
                        )}
                    </section>
                ) : (
                    <section className="album-details">
                        <button
                            onClick={() => setSelectedAlbum(null)}
                            className="back-button"
                            aria-label="Назад к списку альбомов"
                        >
                            ← Назад
                        </button>

                        <div className="album-header">
                            <img
                                src={selectedAlbum.cover}
                                alt={`Обложка альбома ${selectedAlbum.title}`}
                            />
                            <div>
                                <h2>{selectedAlbum.title}</h2>
                                <h3>{selectedAlbum.artist}</h3>
                            </div>
                        </div>

                        <ul className="track-list">
                            {selectedAlbum.tracks.length ? (
                                selectedAlbum.tracks.map((track) => (
                                    <li key={track.id} className="track-item" tabIndex={0}>
                                        <span>{track.title}</span>
                                        <span>{track.duration}</span>
                                    </li>
                                ))
                            ) : (
                                <li>Треков не найдено</li>
                            )}
                        </ul>
                    </section>
                )}
            </main>

            <footer>© 2025 Музыкальный Плеер</footer>

            <style>{`
        /* Reset */
        * {
          box-sizing: border-box;
        }
        body, html, #root, .app {
          margin: 0; padding: 0; height: 100%;
          font-family: "Segoe UI", Tahoma, Geneva, Verdana, sans-serif;
          background-color: #0d0d0d;
          color: #ddd;
          display: flex;
          flex-direction: column;
          min-height: 100vh;
        }

        header {
          padding: 20px 24px;
          background: #1a1a1a;
          box-shadow: 0 2px 8px rgba(80, 60, 140, 0.7);
          user-select: none;
          position: sticky;
          top: 0;
          z-index: 100;
        }

        header h1 {
          margin: 0 0 12px;
          font-weight: 800;
          font-size: 2.6rem;
          color: #a492ff;
          text-shadow: 0 0 10px #9178ff99;
        }

        .search {
          width: 100%;
          max-width: 520px;
          padding: 12px 20px;
          border-radius: 50px;
          border: none;
          font-size: 1.1rem;
          outline: none;
          background-color: #292929;
          color: #ccc;
          transition: background-color 0.3s ease;
          box-shadow: inset 0 0 10px #6c5dd3;
        }

        .search::placeholder {
          color: #7f7f9a;
        }

        .search:focus {
          background-color: #3a2e66;
          box-shadow: inset 0 0 16px #a08fff;
          color: #eee;
        }

        main {
          flex-grow: 1;
          padding: 28px 48px;
          overflow-y: auto;
        }

        .albums {
          display: grid;
          grid-template-columns: repeat(auto-fill,minmax(180px,1fr));
          gap: 22px;
        }

        .album-card {
          background-color: #222222;
          border-radius: 16px;
          overflow: hidden;
          cursor: pointer;
          box-shadow: 0 5px 25px rgba(108, 93, 211, 0.4);
          transition: transform 0.3s ease, box-shadow 0.3s ease;
          display: flex;
          flex-direction: column;
          user-select: none;
        }

        .album-card:hover, .album-card:focus {
          transform: translateY(-8px);
          box-shadow: 0 10px 40px rgba(135, 125, 255, 0.8);
          outline: none;
        }

        .album-card img {
          width: 100%;
          height: 180px;
          object-fit: cover;
          border-bottom: 3px solid #7a6fff;
          filter: drop-shadow(0 0 6px #7a6fffcc);
          transition: filter 0.3s ease;
