import React, { useEffect, useState } from "react";

export default function SpotifyClone() {
    const [search, setSearch] = useState("");
    const [selectedAlbum, setSelectedAlbum] = useState(null);
    const [iconUrl, setIconUrl] = useState("");
    const [albums, setAlbums] = useState([
        {
            id: 1,
            title: "After Hours",
            artist: "The Weeknd",
            cover: "", // временно пусто, заменим после загрузки
            tracks: [
                { id: 101, title: "Blinding Lights", duration: "3:20" },
                { id: 102, title: "Save Your Tears", duration: "3:35" },
                { id: 103, title: "In Your Eyes", duration: "3:58" },
            ],
        },
        {
            id: 2,
            title: "Future Nostalgia",
            artist: "Dua Lipa",
            cover: "https://i.scdn.co/image/ab67616d0000b2736f1e248b9c2d9d93637e23e6",
            tracks: [
                { id: 201, title: "Don't Start Now", duration: "3:03" },
                { id: 202, title: "Physical", duration: "3:13" },
                { id: 203, title: "Levitating", duration: "3:23" },
            ],
        },
    ]);

    useEffect(() => {
        fetch("http://localhost:8080/api/icon")
            .then((res) => res.text())
            .then((url) => {
                setIconUrl(url);
                // обновляем обложку у первого альбома
                setAlbums((prev) =>
                    prev.map((album) =>
                        album.id === 1 ? { ...album, cover: url } : album
                    )
                );
            })
            .catch((err) => {
                console.error("Ошибка загрузки иконки:", err);
            });
    }, []);

    const filteredAlbums = albums
        .map((album) => ({
            ...album,
            tracks: album.tracks.filter((t) =>
                t.title.toLowerCase().includes(search.toLowerCase())
            ),
        }))
        .filter(
            (album) =>
                album.title.toLowerCase().includes(search.toLowerCase()) ||
                album.artist.toLowerCase().includes(search.toLowerCase()) ||
                album.tracks.length > 0
        );



