                    {!selectedAlbum ? (
                        <div className="albums-grid">
                            {filteredAlbums.length ? (
                                filteredAlbums.map((album) => (
                                    <div
                                        key={album.id}
                                        className="album-card"
                                        onClick={() => setSelectedAlbum(album)}
                                        tabIndex={0}
                                        role="button"
                                        aria-label={`Открыть альбом ${album.title}`}
