// App.jsx
import React, { useState, useEffect } from "react";

const sampleAlbums = [
  {
    id: 1,
    title: "Магия Звука",
    artist: "DJ Cool",
    cover: "https://picsum.photos/200?random=1",
    tracks: [
      { id: 11, title: "Волшебный бит", duration: "3:45" },
      { id: 12, title: "Ночная прогулка", duration: "4:20" },
    ],
  },
  {
    id: 2,
    title: "Летний Ветер",
    artist: "The Breeze",
    cover: "https://picsum.photos/200?random=2",
    tracks: [
      { id: 21, title: "Солнечный день", duration: "3:12" },
      { id: 22, title: "Вечер на пляже", duration: "5:01" },
    ],
  },
  // Добавь ещё альбомы...
];

export default function App() {
  const [albums, setAlbums] = useState(sampleAlbums);
  const [selectedAlbum, setSelectedAlbum] = useState(null);
  const [search, setSearch] = useState("");

  // Фильтрация по поиску (альбомы + треки)
  const filteredAlbums = albums
      .map((album) => ({
        ...album,
        tracks: album.tracks.filter((t) =>
            t.title.toLowerCase().includes(search.toLowerCase())
        ),
      }))
      .filter(
          (album) =>
              album.title.toLowerCase().includes(search.toLowerCase()) ||
              album.artist.toLowerCase().includes(search.toLowerCase()) ||
              album.tracks.length > 0
      );

  return (
      <div className="app">
        <header>
          <h1>Музыкальный плеер</h1>
          <input
              type="search"
              placeholder="Искать альбом, исполнителя или трек..."
              value={search}
              onChange={(e) => setSearch(e.target.value)}
              className="search-input"
          />
        </header>

        <main>
          {!selectedAlbum && (
              <section className="album-list">
                {filteredAlbums.length ? (
                    filteredAlbums.map((album) => (
                        <div
                            key={album.id}
                            className="album-card"
                            onClick={() => setSelectedAlbum(album)}
                            tabIndex={0}
                            role="button"
                            aria-label={`Открыть альбом ${album.title} исполнителя ${album.artist}`}
                        >
                          <img src={album.cover} alt={`${album.title} обложка`} />
                          <div className="album-info">
                            <h3>{album.title}</h3>
                            <p>{album.artist}</p>
                          </div>
                        </div>
                    ))
                ) : (
                    <p>По вашему запросу ничего не найдено</p>
                )}
              </section>
          )}

          {selectedAlbum && (
              <section className="album-details">
                <button onClick={() => setSelectedAlbum(null)} className="back-btn">
                  ← Назад к альбомам
                </button>
                <div className="album-header">
                  <img src={selectedAlbum.cover} alt={`${selectedAlbum.title} обложка`} />
                  <div>
                    <h2>{selectedAlbum.title}</h2>
                    <h4>{selectedAlbum.artist}</h4>
                  </div>
                </div>
                <ul className="track-list">
                  {selectedAlbum.tracks.length ? (
                      selectedAlbum.tracks.map((track) => (
                          <li key={track.id} className="track-item">
                            <span>{track.title}</span>
                            <span>{track.duration}</span>
                          </li>
                      ))
                  ) : (
                      <li>Нет треков по вашему запросу</li>
                  )}
                </ul>
              </section>
          )}
        </main>

        <footer>© 2025 Твой Музыкальный Плеер</footer>

        <style>{`
        * {
          box-sizing: border-box;
        }
        body,html,#root,.app {
          margin: 0; padding: 0; height: 100%;
          font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
          background: linear-gradient(135deg, #1DB954 0%, #191414 100%);
          color: #fff;
          display: flex;
          flex-direction: column;
          min-height: 100vh;
        }
        header {
          padding: 20px;
          text-align: center;
          background: rgba(0,0,0,0.3);
          box-shadow: 0 2px 6px rgba(0,0,0,0.6);
          position: sticky;
          top: 0;
          z-index: 100;
        }
        header h1 {
          margin: 0 0 10px;
          font-weight: 700;
          letter-spacing: 1.5px;
          font-size: 2.5rem;
          user-select: none;
        }
        .search-input {
          width: 100%;
          max-width: 500px;
          padding: 12px 20px;
          border-radius: 50px;
          border: none;
          font-size: 1rem;
          outline: none;
          transition: background-color 0.3s ease;
          background: rgba(255 255 255 / 0.15);
          color: #fff;
        }
        .search-input::placeholder {
          color: #ccc;
        }
        .search-input:focus {
          background: rgba(255 255 255 / 0.3);
        }
        main {
          flex-grow: 1;
          padding: 20px 40px;
          overflow-y: auto;
        }
        .album-list {
          display: grid;
          grid-template-columns: repeat(auto-fill,minmax(180px,1fr));
          gap: 20px;
        }
        .album-card {
          background: rgba(0,0,0,0.4);
          border-radius: 15px;
          cursor: pointer;
          overflow: hidden;
          box-shadow: 0 6px 20px rgb(27 185 84 / 0.4);
          transition: transform 0.3s ease, box-shadow 0.3s ease;
          display: flex;
          flex-direction: column;
          user-select: none;
        }
        .album-card:hover, .album-card:focus {
          transform: translateY(-8px);
          box-shadow: 0 10px 30px rgb(27 185 84 / 0.8);
          outline: none;
        }
        .album-card img {
          width: 100%;
          height: 180px;
          object-fit: cover;
          border-bottom: 2px solid #1DB954;
        }
        .album-info {
          padding: 15px;
          text-align: center;
        }
        .album-info h3 {
          margin: 0 0 5px;
          font-weight: 700;
          font-size: 1.2rem;
          text-shadow: 0 1px 4px rgba(0,0,0,0.7);
        }
        .album-info p {
          margin: 0;
          font-size: 0.9rem;
          color: #a1e3a1;
          font-weight: 500;
          text-shadow: 0 1px 3px rgba(0,0,0,0.5);
        }
        .album-details {
          max-width: 700px;
          margin: 0 auto;
          background: rgba(0,0,0,0.45);
          border-radius: 20px;
          padding: 30px;
          box-shadow: 0 10px 40px rgb(27 185 84 / 0.6);
          user-select: none;
          animation: fadeIn 0.5s ease forwards;
        }
        .back-btn {
          background: none;
          border: none;
          color: #1DB954;
          font-weight: 700;
          font-size: 1rem;
          cursor: pointer;
          margin-bottom: 20px;
          text-shadow: 0 1px 2px rgba(0,0,0,0.5);
          transition: color 0.3s ease;
        }
        .back-btn:hover {
          color: #16a34a;
        }
        .album-header {
          display: flex;
          gap: 20px;
          align-items: center;
          margin-bottom: 30px;
        }
        .album-header img {
          width: 180px;
          height: 180px;
          border-radius: 12px;
          object-fit: cover;
          box-shadow: 0 6px 25px rgb(27 185 84 / 0.9);
        }
        .album-header h2 {
          margin: 0;
          font-size: 2.8rem;
          font-weight: 900;
          text-shadow: 0 2px 6px rgba(0,0,0,0.8);
        }
        .album-header h4 {
          margin: 6px 0 0;
          font-weight: 600;
          color: #a1e3a1;
          text-shadow: 0 1px 3px rgba(0,0,0,0.5);
        }
        .track-list {
          list-style: none;
          padding: 0;
          margin: 0;
          border-top: 2px solid #1DB954;
        }
        .track-item {
          display: flex;
          justify-content: space-between;
          padding: 12px 15px;
          border-bottom: 1px solid rgba(255 255 255 / 0.1);
          font-weight: 500;
          font-size: 1.1rem;
          cursor: pointer;
          transition: background-color 0.25s ease;
          border-radius: 8px;
        }
        .track-item:hover {
          background-color: rgba(27, 185, 84, 0.15);
        }
        footer {
          text-align: center;
          padding: 15px;
          font-size: 0.9rem;
          background: rgba(0,0,0,0.3);
          user-select: none;
          box-shadow: 0 -2px 6px rgba(0,0,0,0.6);
        }
        @keyframes fadeIn {
          from {opacity: 0; transform: translateY(15px);}
          to {opacity: 1; transform: translateY(0);}
        }
      `}</style>
      </div>
  );
}
